Select
SUM(A."Amount of Billing Reversed"),
count(a.contract)
From

(Select 
	BDMO.PARTNER,
    BDMO.CONTRACT,
    BDMO.CREATED_DATE,
    BDMO.proposed_date,
 	BDMO.datediff,
	BDMO.PROCESS,
	BDMO.AID,
	BDMO.CONTACT_NOTE,
	BDMO.installation,
	BDMO.POD,
	BDMO.BDMO_MOVE_OUT,
	EV3.CONTRACT2,
	to_date(EV3.CONTRACT2_START),
	to_date(EV3.CONTRACT2_END),
	EV3.CONTRACT2_INSTALLATION,
	EV3.NEW_ACC,
	Days_between(BDMO.BDMO_MOVE_OUT,EV3.CONTRACT2_START) as days_gap,
	RVE.OC_NUMBER_BILLS_REVERSED as "Number of Bills Reversed",
	RVE.OC_BILL_AMOUNT_REVERSED as "Amount of Billing Reversed"

 from
(
SELECT  DISTINCT --Getting BDMO data
	D.PARTNER,
    D.CONTRACT,
    TO_DATE(D.CREATED_DATE) AS CREATED_DATE,
    TO_DATE(D.proposed_date) AS proposed_date,
 	Days_between(D.proposed_date,D.CREATED_DATE) as datediff,
	D.CREATION_TIME,
	D.PROCESS,
	D.AID,
	D.STATUS,
	D.MESSAGE,
	D.CONTACT_NOTE,
	D.USER_SEL,
	EV.Anlage as installation,
	POD.EXT_UI as POD,
	to_date(EV.AUSZDAT) as BDMO_MOVE_OUT
   
FROM COMMERCIAL.ZCMKTT354 D
JOIN
(
SELECT
	A.PARTNER,
	A.CONTRACT,
	A.CREATED_DATE,
	MIN(A.CREATION_TIME) AS MAX_TIME
FROM
COMMERCIAL.ZCMKTT354 A
JOIN
(
SELECT 
	PARTNER,
	CONTRACT,
	MIN(CREATED_DATE) as MAX_DATE
	
FROM
COMMERCIAL.ZCMKTT354
GROUP BY
	PARTNER,
CONTRACT ) B

ON  A.PARTNER = B.PARTNER 
AND A.CONTRACT = B.CONTRACT 
AND A.CREATED_DATE = B.MAX_DATE
GROUP BY
	A.PARTNER,
	A.CONTRACT,
	A.CREATED_DATE) C
ON  D.PARTNER = C.PARTNER 
AND D.CONTRACT = C.CONTRACT 
AND D.CREATED_DATE = C.CREATED_DATE 
AND D.CREATION_TIME =  C.MAX_TIME
Left join commercial.ever EV
      on D.Contract = EV.vertrag
left join COMMERCIAL.EUIINSTLN INST
	on EV.ANLAGE = INST.ANLAGE
left join customer.euitrans POD
	on INST.INT_UI = POD.INT_UI	
	WHERE D."PROCESS" = 'BDMO' 
	

 
